class ImageControllerTest extends \PHPUnit_Framework_TestCase
{
    /**
     * Setup testing
     *
     * @return  $this
     * @since   1.0
     */
    protected function setUp()
    {
        mkdir(__DIR__ . '/Media');
        copy(__DIR__ . '/TestMedia/Sample.gif', __DIR__ . '/Media/Sample.gif');
        copy(__DIR__ . '/TestMedia/Sample.jpg', __DIR__ . '/Media/Sample.jpg');
        copy(__DIR__ . '/TestMedia/Sample.png', __DIR__ . '/Media/Sample.png');
        return $this;
    }

    /**
     * @covers  Molajo\Controller\ImageController::__construct
     * @covers  Molajo\Controller\ImageController::getImage
     * @covers  Molajo\Controller\ImageController::getImagePlaceholder
     * @covers  Molajo\Controller\ImageController::setImageObject
     * @covers  Molajo\Controller\ImageController::setImageSize
     * @covers  Molajo\Controller\ImageController::resizeImage
     *
     * @return  $this
     * @since   1.0
     */
    public function testGetGifImage()
    {
        $media_folder  = __DIR__ . '/Media';
        $standard_size = array();
        $standard_type = array();
        $default_size  = 'normal';
        $default_type  = 'auto';

        $image_controller = new ImageController(
            $media_folder, $standard_size, $standard_type, $default_size, $default_type
        );

        $image_filename = $media_folder . '/' . 'Sample.gif';
        $results        = $image_controller->getImage($image_filename);

        $this->assertEquals($image_filename, $results->original_filename);
        $this->assertEquals('normal', $results->size);
        $this->assertEquals('auto', $results->type);
        $this->assertEquals($media_folder, $results->original_path);
        $this->assertEquals('Sample.gif', $results->original_basename);
        $this->assertEquals('gif', $results->original_file_extension);
        $this->assertEquals(450, $results->original_width);
        $this->assertEquals(450, $results->original_height);
        $this->assertEquals($image_filename, $results->filename);
        $this->assertEquals($media_folder, $results->path);
        $this->assertEquals('Sample.gif', $results->basename);
        $this->assertEquals('gif', $results->file_extension);
        $this->assertEquals(450, $results->width);
        $this->assertEquals(450, $results->height);
        $this->assertEquals(0, $results->x);
        $this->assertEquals(0, $results->y);
        $this->assertEquals(
            'data:image/gif;base64,',
            $results->base64
        );

        return $this;
    }

